
public class E1_A6_Bucles {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Ejercicio 1 A6_Bucles
		// Utiliza un contador para almacenar en él la suma de todos los números del 1 al 100.
		
		
		int suma = 0;
		
		for (int i = 1; i <= 100; i++) {
			
			suma = suma + i;
			System.out.println(suma);
		}

		
		
	}

}
