		// ejercicio 1: (se pone "final" porque es una constante que no cambia el número pi, que es 3,14 siempre)
		final float Pi = 3.14f;
		System.out.println(Pi);
		
		// ejercicio 1.1
		
		final float E = 2.71f;
		System.out.println(E);
		
		// Resultado de 8+3
		int suma = 8 + 3;
		System.out.println(suma);
		
		// Resultado de 3.14*2
		float multiplicacion = 3.14f * 2;
		System.out.println(multiplicacion);
		
		// Resultado de 14.38541635 – 6.3874169365
		float resta1 = 14.38541635f;
		float resta2 = 6.3874169365f;
		System.out.println(resta1 - resta2);

		// Resultado de 6.12354*2
		float multiplicacion1 = 6.12354f * 2;
