		System.out.println(vuestroNombre);
		
		// Vuestro número de teléfono
		int numeroDeTelefono = 650849307;
		System.out.println(numeroDeTelefono);
		
		// Vuestro DNI (hay 2 maneras, pero usar la del String)
		int vuestroDni = 48703997;
		char letraDni = 'W';
		System.out.println(vuestroDni + "" + letraDni);
		
		String vuestroDni1 = "48703997W";
		System.out.println(vuestroDni1);
		
		
		// Suma de 9 y 5
		int suma1 = 9 +5;
