
public class BoletinCondicionalesBucles_E1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 20;
		int y = 30;
	
		int variableMayor;
		
		if (x > y) {
			variableMayor = x;
			System.out.println(variableMayor);
		}
		
		if (y > x) {
			variableMayor = y;
			System.out.println(variableMayor);
		}
		
		if (x == y) {
			variableMayor = y;
			System.out.println(variableMayor);
		}	

		

	}

}
